Here is an implementation of a red-black tree in C++:

```
#include <iostream>

using namespace std;

enum Color {RED, BLACK};

struct Node {
    int val;
    Color color;
    Node *left, *right, *parent;

    Node() {}

    Node(int v, Color c) {
        val = v;
        color = c;
        left = right = parent = NULL;
    }
};

class RedBlackTree {
    private:
        Node *root;

    public:
        RedBlackTree() { root = NULL; }

        /* Helper functions */
        Node* grandparent(Node *n) {
            if (n && n->parent) { return n->parent->parent; }
            else { return NULL; }
        }

        Node* uncle(Node *n) {
            Node *g = grandparent(n);
            if (!g) { return NULL; }
            if (n->parent == g->left) { return g->right; }
            else { return g->left; }
        }

        Node* sibling(Node *n) {
            if (!n || !n->parent) { return NULL; }
            if (n == n->parent->left) { return n->parent->right; }
            else { return n->parent->left; }
        }

        void fix_tree(Node *n) {
            while (n != root && n->color == RED && n->parent->color == RED) {
                Node *p = n->parent;
                Node *g = grandparent(n);
                Node *u = uncle(n);

                if (u && u->color == RED) {
                    p->color = BLACK;
                    u->color = BLACK;
                    g->color = RED;
                    n = g;
                } else {
                    if (p == g->left) {
                        if (n == p->right) {
                            n = p;
                            left_rotate(n);
                        }
                        p->color = BLACK;
                        g->color = RED;
                        right_rotate(g);
                    } else {
                        if (n == p->left) {
                            n = p;
                            right_rotate(n);
                        }
                        p->color = BLACK;
                        g->color = RED;
                        left_rotate(g);
                    }
                }
            }
            root->color = BLACK;
        }

        void left_rotate(Node *n) {
            Node *r = n->right;
            n->right = r->left;
            if (r->left) { r->left->parent = n; }
            r->parent = n->parent;
            if (!n->parent) { root = r; }
            else if (n == n->parent->left) { n->parent->left = r; }
            else { n->parent->right = r; }
            r->left = n;
            n->parent = r;
        }

        void right_rotate(Node *n) {
            Node *l = n->left;
            n->left = l->right;
            if (l->right) { l->right->parent = n; }
            l->parent = n->parent;
            if (!n->parent) { root = l; }
            else if (n == n->parent->left) { n->parent->left = l; }
            else { n->parent->right = l; }
            l->right = n;
            n->parent = l;
        }

        void insert_node(int val) {
            Node *n = new Node(val, RED);
            if (!root) { root = n; }
            else {
                Node *current = root;
                Node *parent = NULL;

                while (current) {
                    parent = current;
                    if (val < current->val) { current = current->left; }
                    else { current = current->right; }
                }
                n->parent = parent;
                if (val < parent->val) { parent->left = n; }
                else { parent->right = n; }
                fix_tree(n);
            }
        }

        Node* search_node(int val) {
            Node *current = root;
            while (current && current->val != val) {
                if (val < current->val) { current = current->left; }
                else { current = current->right; }
            }
            return current;
        }

        void inorder(Node *n) {
            if (!n) { return; }
            inorder(n->left);
            cout << n->val << " ";
            inorder(n->right);
        }

        void print_tree() {
            inorder(root);
            cout << endl;
        }
};
```

